import React, { useState } from 'react'
import './Slider.css'
import { UilArrowLeft, UilArrowRight } from '@iconscout/react-unicons'
import { SliderItems } from '../data'


const Slider = () => {

    const [slideIndex, setSlideIndex] = useState(0)
    const slide = document.querySelectorAll('.slide-container')

    const handleLeftClick = () =>{
        setSlideIndex(slideIndex > 0 ? slideIndex - 1 : 2)
        SliderItems.map(item =>{
            item.id = slideIndex
        })
    }
    const handleRightClick = () =>{
        setSlideIndex(slideIndex < 2 ? slideIndex + 1 : 0)
    }


    return (
        <div className='slider-container'>
            <div className="sli-arrow-left" onClick={handleLeftClick()}>
                <UilArrowLeft />
            </div>
            <div className="slide-container" slideIndex={slideIndex}>
                {SliderItems.map(item=>(
                    <div className="slide">
                    <div className="slide-img">
                        {item.img}
                    </div>
                    <div className="slide-info">
                        <h1 className="slide-info-title">{item.title}</h1>
                        <p className="slide-info-desc">{item.desc}</p>
                        <button className="slide-info-button">SHOP NOW</button>
                    </div>
                </div>
                ))}
            </div>
            <div className="sli-arrow-right">
                <UilArrowRight />
            </div>
        </div>
    )
}

export default Slider
